import React, { useState, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF, Html } from '@react-three/drei';
import './index.css';

// Component to load and display the GLB model for the selected suite.
const SuiteModel = ({ modelPath }) => {
  const { scene } = useGLTF(modelPath);
  return <primitive object={scene} />;
};

const App = () => {
  // Define suite data.
  const roomsData = [
    { id: 1, title: 'Suite 1', modelPath: '/models/suite1.glb' },
    { id: 2, title: 'Suite 2', modelPath: '/models/suite2.glb' },
    { id: 3, title: 'Suite 3', modelPath: '/models/suite3.glb' },
  ];

  const [selectedRoom, setSelectedRoom] = useState(roomsData[0]);

  // Handler for button clicks to update the selected room.
  const handleSelectRoom = (id) => {
    const room = roomsData.find((r) => r.id === id);
    setSelectedRoom(room);
  };

  return (
    <div className="App">
      {/* Navigation Buttons */}
      <div className="button-container">
        {roomsData.map((room) => (
          <button key={room.id} onClick={() => handleSelectRoom(room.id)}>
            {room.title}
          </button>
        ))}
      </div>

      {/* 3D Model Viewer */}
      <Canvas>
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />

        {/* Wrap fallback in <Html> to avoid plain HTML elements inside Canvas */}
        <Suspense fallback={<Html center><div>Loading 3D Model...</div></Html>}>
          <SuiteModel modelPath={selectedRoom.modelPath} />
        </Suspense>

        <OrbitControls />
      </Canvas>
    </div>
  );
};

export default App;
