import React, { useState, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF, Html } from '@react-three/drei';
import './index.css';

// Navigation component using icon buttons
const RoomNav = ({ rooms, selectedRoom, onSelectRoom }) => {
  return (
    <div className="room-nav-container">
      {rooms.map((room) => (
        <button
          key={room.id}
          className={`room-nav-item ${selectedRoom.id === room.id ? 'active' : ''}`}
          onClick={() => onSelectRoom(room)}
        >
          <img src={room.icon} alt={room.title} className="room-icon" />
        </button>
      ))}
    </div>
  );
};

// Component to load and display the 3D model for the selected suite.
const SuiteModel = ({ modelPath }) => {
  const { scene } = useGLTF(modelPath);
  return <primitive object={scene} />;
};

const App = () => {
  // Room data with id, title, model path, and icon path.
  const roomsData = [
    { id: 1, title: 'Suite 1', modelPath: '/models/suite1.glb', icon: '/icons/suite1.svg' },
    { id: 2, title: 'Suite 2', modelPath: '/models/suite2.glb', icon: '/icons/suite2.svg' },
    { id: 3, title: 'Suite 3', modelPath: '/models/suite3.glb', icon: '/icons/suite3.svg' },
  ];

  // Default selected room is the first suite.
  const [selectedRoom, setSelectedRoom] = useState(roomsData[0]);

  return (
    <div className="App">
      {/* Icon-based navigation bar */}
      <RoomNav rooms={roomsData} selectedRoom={selectedRoom} onSelectRoom={setSelectedRoom} />

      {/* 3D Model Viewer Container */}
      <div className="model-viewer">
        <Canvas camera={{ position: [0, 0, 5], fov: 75 }}>
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} />
          <Suspense fallback={<Html center><div>Loading 3D Model...</div></Html>}>
            {/* No dynamic key here, so useGLTF caching will allow the model to reappear */}
            <SuiteModel modelPath={selectedRoom.modelPath} />
          </Suspense>
          <OrbitControls />
        </Canvas>
      </div>
    </div>
  );
};

export default App;
