import React, { useState, Suspense, useRef, useEffect } from 'react';
import { Canvas } from '@react-three/fiber';
import {
  OrbitControls,
  PerspectiveCamera,
  OrthographicCamera,
  useGLTF,
  Html
} from '@react-three/drei';
import './index.css';

// Room Navigation Component (using your original button style)
const RoomNav = ({ rooms, selectedRoom, onSelectRoom }) => {
  return (
    <div className="room-nav-container">
      {rooms.map((room) => (
        <button
          key={room.id}
          className={`room-nav-item ${selectedRoom.id === room.id ? 'active' : ''}`}
          onClick={() => onSelectRoom(room)}
        >
          <div className="room-title">{room.title}</div>
        </button>
      ))}
    </div>
  );
};

// Component to load and display the 3D model for the selected suite.
const SuiteModel = ({ modelPath }) => {
  const { scene } = useGLTF(modelPath);
  return <primitive object={scene} />;
};

// Control Panel for zoom, display mode, and Person View toggle/drop‑down.
const ControlPanel = ({
  onZoomIn,
  onZoomOut,
  displayMode,
  setDisplayMode,
  controlMode,
  toggleControlMode,
  personViewPreset,
  setPersonViewPreset,
  personViewOptions
}) => {
  return (
    <div className="control-panel">
      <button onClick={onZoomIn} className="zoom-button">
        <img src="/icons/zoom_in.svg" alt="Zoom In" />
      </button>
      <button onClick={onZoomOut} className="zoom-button">
        <img src="/icons/zoom_out.svg" alt="Zoom Out" />
      </button>
      <div className="display-toggle-tabs">
        <button
          className={`display-tab ${displayMode === '3D' ? 'active' : ''}`}
          onClick={() => setDisplayMode('3D')}
        >
          3D
        </button>
        <button
          className={`display-tab ${displayMode === '2D' ? 'active' : ''}`}
          onClick={() => setDisplayMode('2D')}
        >
          2D
        </button>
      </div>
      <button
        onClick={toggleControlMode}
        className={`control-button ${controlMode === 'person' ? 'active' : ''}`}
      >
        <img src="/icons/person_view.svg" alt="Person View" className="person-view-icon" />
        Person View
      </button>
      {controlMode === 'person' && (
        <select
          className="person-view-dropdown"
          value={personViewPreset}
          onChange={(e) => setPersonViewPreset(e.target.value)}
        >
          {personViewOptions.map((option) => (
            <option key={option.label} value={option.label}>
              {option.label}
            </option>
          ))}
        </select>
      )}
    </div>
  );
};

const App = () => {
  // Room (suite) data.
  const roomsData = [
    { id: 1, title: 'Suite 1', modelPath: '/models/suite1.glb' },
    { id: 2, title: 'Suite 2', modelPath: '/models/suite2.glb' },
    { id: 3, title: 'Suite 3', modelPath: '/models/suite3.glb' },
  ];

  // Person View presets (each with a label and a camera position).
  const personViewOptions = [
    { label: "Standard", position: [0, 1.6, 5] },
    { label: "Corner", position: [2, 1.6, 5] },
    { label: "Back", position: [0, 1.6, -5] }
  ];

  // State for selected room, display mode, control mode, zoom factor, and person view preset.
  const [selectedRoom, setSelectedRoom] = useState(roomsData[0]);
  const [displayMode, setDisplayMode] = useState("3D"); // "3D" uses Perspective, "2D" uses Orthographic.
  const [controlMode, setControlMode] = useState("orbit"); // "orbit" or "person"
  const [zoomFactor, setZoomFactor] = useState(1);
  const [personViewPreset, setPersonViewPreset] = useState("Standard");

  // Refs for the camera and OrbitControls.
  const cameraRef = useRef();
  const controlsRef = useRef();

  // When switching rooms, control mode, or person view preset, reset zoom and update camera position.
  useEffect(() => {
    setZoomFactor(1);
    if (controlsRef.current) {
      if (controlMode === "person") {
        // In person view, we aim for eye-level.
        controlsRef.current.target.set(0, 1.6, 0);
      } else {
        controlsRef.current.target.set(0, 0, 0);
      }
      controlsRef.current.reset();
    }
    if (cameraRef.current && displayMode === "3D") {
      if (controlMode === "person") {
        const preset = personViewOptions.find(opt => opt.label === personViewPreset);
        if (preset) {
          cameraRef.current.position.set(...preset.position);
        } else {
          cameraRef.current.position.set(0, 1.6, 5);
        }
      } else {
        cameraRef.current.position.set(0, 0, 5);
      }
    }
  }, [selectedRoom, controlMode, displayMode, personViewPreset]);

  // Update camera position when zoom changes (for 3D mode).
  useEffect(() => {
    if (cameraRef.current && displayMode === "3D") {
      if (controlMode === "orbit") {
        cameraRef.current.position.z = 5 * zoomFactor;
      } else if (controlMode === "person") {
        const preset = personViewOptions.find(opt => opt.label === personViewPreset);
        if (preset) {
          // Scale the z component only.
          cameraRef.current.position.set(
            preset.position[0],
            preset.position[1],
            preset.position[2] * zoomFactor
          );
        }
      }
    }
  }, [zoomFactor, displayMode, controlMode, personViewPreset]);

  const zoomIn = () => setZoomFactor((prev) => Math.max(prev * 0.9, 0.5));
  const zoomOut = () => setZoomFactor((prev) => prev / 0.9);
  const toggleControlMode = () => {
    setControlMode((prev) => (prev === "orbit" ? "person" : "orbit"));
  };

  return (
    <div className="App">
      {/* Room Navigation */}
      <div className="room-nav-container">
        {roomsData.map((room) => (
          <button
            key={room.id}
            className={`room-nav-item ${selectedRoom.id === room.id ? 'active' : ''}`}
            onClick={() => setSelectedRoom(room)}
          >
            <div className="room-title">{room.title}</div>
          </button>
        ))}
      </div>

      {/* 3D Model Viewer Container */}
      <div className="model-viewer">
        <Canvas>
          {displayMode === "3D" ? (
            <PerspectiveCamera
              makeDefault
              ref={cameraRef}
              position={
                controlMode === "person"
                  ? personViewOptions.find(opt => opt.label === personViewPreset)?.position || [0, 1.6, 5]
                  : [0, 0, 5 * zoomFactor]
              }
              fov={75}
            />
          ) : (
            <OrthographicCamera
              makeDefault
              ref={cameraRef}
              position={[0, 50, 0]}
              zoom={50 * zoomFactor}
              near={0.1}
              far={1000}
            />
          )}
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} />
          <Suspense fallback={<Html center><div>Loading 3D Model...</div></Html>}>
            <SuiteModel modelPath={selectedRoom.modelPath} />
          </Suspense>
          <OrbitControls ref={controlsRef} />
        </Canvas>
      </div>

      {/* Control Panel for Zoom, Display Mode Tabs, and Person View Toggle */}
      <div className="control-panel">
        <button onClick={zoomIn} className="zoom-button">
          <img src="/icons/zoom_in.svg" alt="Zoom In" />
        </button>
        <button onClick={zoomOut} className="zoom-button">
          <img src="/icons/zoom_out.svg" alt="Zoom Out" />
        </button>
        <div className="display-toggle-tabs">
          <button
            className={`display-tab ${displayMode === '3D' ? 'active' : ''}`}
            onClick={() => setDisplayMode('3D')}
          >
            3D
          </button>
          <button
            className={`display-tab ${displayMode === '2D' ? 'active' : ''}`}
            onClick={() => setDisplayMode('2D')}
          >
            2D
          </button>
        </div>
        <button
          onClick={toggleControlMode}
          className={`control-button ${controlMode === 'person' ? 'active' : ''}`}
        >
          <img src="/icons/person_view.svg" alt="Person View" className="person-view-icon" />
          Person View
        </button>
        {controlMode === 'person' && (
          <select
            className="person-view-dropdown"
            value={personViewPreset}
            onChange={(e) => setPersonViewPreset(e.target.value)}
          >
            {personViewOptions.map((option) => (
              <option key={option.label} value={option.label}>
                {option.label}
              </option>
            ))}
          </select>
        )}
      </div>
    </div>
  );
};

export default App;
