import React, { useState, Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { OrbitControls, useGLTF, Html } from '@react-three/drei';
import './index.css';

// Navigation component: displays room cards for selection.
const RoomNav = ({ rooms, selectedRoom, onSelectRoom }) => {
  return (
    <div className="room-nav-container">
      {rooms.map((room) => (
        <button
          key={room.id}
          className={`room-nav-item ${selectedRoom.id === room.id ? 'active' : ''}`}
          onClick={() => onSelectRoom(room)}
        >
          <div className="room-image">
            <img src={room.image} alt={room.title} />
          </div>
          <div className="room-title">{room.title}</div>
        </button>
      ))}
    </div>
  );
};

// Component to load and display the 3D model (suite)
const SuiteModel = ({ modelPath }) => {
  const { scene } = useGLTF(modelPath);
  return <primitive object={scene} />;
};

const App = () => {
  // Define room data with id, title, preview image, and model path.
  const roomsData = [
    { id: 1, title: 'Suite 1', image: '/images/suite1.jpg', modelPath: '/models/suite1.glb' },
    { id: 2, title: 'Suite 2', image: '/images/suite2.jpg', modelPath: '/models/suite2.glb' },
    { id: 3, title: 'Suite 3', image: '/images/suite3.jpg', modelPath: '/models/suite3.glb' },
  ];

  // Set default room to the first one.
  const [selectedRoom, setSelectedRoom] = useState(roomsData[0]);

  return (
    <div className="App">
      {/* Navigation Bar (fixed at the top) */}
      <RoomNav rooms={roomsData} selectedRoom={selectedRoom} onSelectRoom={setSelectedRoom} />

      {/* 3D Model Viewer Container */}
      <div className="model-viewer">
        <Canvas>
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} />
          {/* Adding a key forces the model to remount when changed */}
          <Suspense fallback={<Html center><div>Loading 3D Model...</div></Html>}>
            <SuiteModel key={selectedRoom.modelPath} modelPath={selectedRoom.modelPath} />
          </Suspense>
          <OrbitControls />
        </Canvas>
      </div>
    </div>
  );
};

export default App;
