import React, { Suspense } from 'react';
import { Canvas } from '@react-three/fiber';
import { useGLTF, OrbitControls } from '@react-three/drei';
// Component to load and display the GLB model
function MyModel() {
  const { scene } = useGLTF('/models/yo.glb'); // Ensure path is correct
  return <primitive object={scene} />;
}

// Main App component
function App() {
  return (
    // Ensure div is not inside Canvas, it wraps around Canvas instead
    <div style={{ height: '100vh', width: '100%' }}>
      {/* Canvas component renders the 3D scene */}
      <Canvas>
        {/* Lights */}
        <ambientLight intensity={0.5} />
        <pointLight position={[10, 10, 10]} />

        {/* Suspense with fallback to handle model loading */}
        <Suspense fallback={<div>Loading...</div>}>
          <MyModel />
        </Suspense>

        {/* OrbitControls for interactive scene navigation */}
        <OrbitControls />
      </Canvas>
    </div>
  );
}

export default App;
