import React, { useState, Suspense, useRef, useEffect } from 'react';
import { Canvas } from '@react-three/fiber';
import {
  OrbitControls,
  PointerLockControls,
  PerspectiveCamera,
  OrthographicCamera,
  useGLTF,
  Html
} from '@react-three/drei';
import './index.css';

// Navigation Component (RoomNav) using your original button style.
const RoomNav = ({ rooms, selectedRoom, onSelectRoom }) => {
  return (
    <div className="room-nav-container">
      {rooms.map((room) => (
        <button
          key={room.id}
          className={`room-nav-item ${selectedRoom.id === room.id ? 'active' : ''}`}
          onClick={() => onSelectRoom(room)}
        >
          <div className="room-title">{room.title}</div>
        </button>
      ))}
    </div>
  );
};

// Component to load and display the 3D model for the selected suite.
const SuiteModel = ({ modelPath }) => {
  const { scene } = useGLTF(modelPath);
  return <primitive object={scene} />;
};

// Control Panel for zoom controls, display mode toggle (3D/2D), and Person View toggle.
const ControlPanel = ({
  onZoomIn,
  onZoomOut,
  displayMode,
  setDisplayMode,
  controlMode,
  toggleControlMode
}) => {
  return (
    <div className="control-panel">
      <button onClick={onZoomIn} className="zoom-button">
        <img src="/icons/zoom_in.svg" alt="Zoom In" />
      </button>
      <button onClick={onZoomOut} className="zoom-button">
        <img src="/icons/zoom_out.svg" alt="Zoom Out" />
      </button>
      <div className="display-toggle-tabs">
        <button
          className={`display-tab ${displayMode === '3D' ? 'active' : ''}`}
          onClick={() => setDisplayMode('3D')}
        >
          3D
        </button>
        <button
          className={`display-tab ${displayMode === '2D' ? 'active' : ''}`}
          onClick={() => setDisplayMode('2D')}
        >
          2D
        </button>
      </div>
      <button
        onClick={toggleControlMode}
        className={`control-button ${controlMode === 'person' ? 'active' : ''}`}
      >
        Person View
      </button>
    </div>
  );
};

const App = () => {
  // Room (suite) data.
  const roomsData = [
    { id: 1, title: 'Suite 1', modelPath: '/models/suite1.glb' },
    { id: 2, title: 'Suite 2', modelPath: '/models/suite2.glb' },
    { id: 3, title: 'Suite 3', modelPath: '/models/suite3.glb' },
  ];

  // State for selected room, display mode, control mode, and zoom factor.
  const [selectedRoom, setSelectedRoom] = useState(roomsData[0]);
  const [displayMode, setDisplayMode] = useState('3D'); // "3D" for Perspective, "2D" for Orthographic.
  const [controlMode, setControlMode] = useState('orbit'); // "orbit" (default) or "person".
  const [zoomFactor, setZoomFactor] = useState(1);

  // Refs for the camera and OrbitControls.
  const cameraRef = useRef();
  const controlsRef = useRef();

  // When switching rooms, display mode, or control mode, reset zoom and controls.
  useEffect(() => {
    setZoomFactor(1);
    if (controlsRef.current && controlMode === 'orbit') {
      controlsRef.current.reset();
    }
    if (cameraRef.current && displayMode === '3D') {
      if (controlMode === 'orbit') {
        cameraRef.current.position.set(0, 0, 5);
      } else if (controlMode === 'person') {
        // Set camera to a typical eye-level position in the room.
        cameraRef.current.position.set(0, 1.6, 0);
      }
    }
  }, [selectedRoom, displayMode, controlMode]);

  // Update camera position when zoom changes (for 3D and orbit mode).
  useEffect(() => {
    if (cameraRef.current && displayMode === '3D' && controlMode === 'orbit') {
      cameraRef.current.position.z = 5 * zoomFactor;
    }
  }, [zoomFactor, displayMode, controlMode]);

  const zoomIn = () => setZoomFactor((prev) => Math.max(prev * 0.9, 0.5));
  const zoomOut = () => setZoomFactor((prev) => prev / 0.9);
  const toggleControlMode = () => {
    setControlMode((prev) => (prev === 'orbit' ? 'person' : 'orbit'));
  };

  return (
    <div className="App">
      {/* Room Navigation */}
      <RoomNav rooms={roomsData} selectedRoom={selectedRoom} onSelectRoom={setSelectedRoom} />

      {/* 3D Model Viewer */}
      <div className="model-viewer">
        <Canvas>
          {displayMode === '3D' ? (
            <PerspectiveCamera
              makeDefault
              ref={cameraRef}
              position={
                controlMode === 'orbit'
                  ? [0, 0, 5 * zoomFactor]
                  : [0, 1.6, 0] // Person view: camera at room center/eye-level.
              }
              fov={75}
            />
          ) : (
            <OrthographicCamera
              makeDefault
              ref={cameraRef}
              position={[0, 50, 0]}
              zoom={50 * zoomFactor}
              near={0.1}
              far={1000}
            />
          )}
          <ambientLight intensity={0.5} />
          <pointLight position={[10, 10, 10]} />
          <Suspense fallback={<Html center><div>Loading 3D Model...</div></Html>}>
            <SuiteModel modelPath={selectedRoom.modelPath} />
          </Suspense>
          {controlMode === 'orbit' ? (
            <OrbitControls ref={controlsRef} />
          ) : (
            // For person view, we use PointerLockControls.
            <PointerLockControls />
          )}
        </Canvas>
      </div>

      {/* Control Panel for Zoom, Display Mode, and Person View Toggle */}
      <ControlPanel
        onZoomIn={zoomIn}
        onZoomOut={zoomOut}
        displayMode={displayMode}
        setDisplayMode={setDisplayMode}
        controlMode={controlMode}
        toggleControlMode={toggleControlMode}
      />
    </div>
  );
};

export default App;
